mapper.select:
		select * from for_notes a
	    where 1=1
	    and a.theme like CONCAT('%',#{theme},'%')
	    and a.members_id like CONCAT('%',#{membersId},'%')
        and a.parent_modules like CONCAT('%',#{parentmodules},'%') 
        and a.childs_modules like CONCAT('%',#{childsmodules},'%') 
        and DATE_FORMAT(a.releasetime, '%Y-%m-%d')  >= #{beginCreateTime}
        and DATE_FORMAT(a.releasetime, '%Y-%m-%d')  <= #{endCreateTime}
    	order by a.id asc
    	limit #{pn},#{ps}
